    fprintf(stdout, "\x47\x65\x6e\x65\x72\x61\x74\x6f\x72\x20\x61\x73\x20\x6f\x63\x74\x65\x74\x20\x73\x74\x72\x69\x6e\x67\x2c\x20\x63\x6f\x6d\x70\x72\x65\x73\x73\x65\x64\x20\x66\x6f\x72\x6d\x3a\xa\x20\x20\x20\x20\x20");
    for (i = 0; i < len; i++)
        fprintf(stdout, "\x25\x30\x32\x58", buf[i]);

    len =
        EC_POINT_point2oct(group, Q, POINT_CONVERSION_UNCOMPRESSED, buf,
                           sizeof buf, ctx);
    if (len == 0)
        ABORT;
    if (!EC_POINT_oct2point(group, P, buf, len, ctx))
        ABORT;
    if (0 != EC_POINT_cmp(group, P, Q, ctx))
        ABORT;
    fprintf(stdout, "\xa\x47\x65\x6e\x65\x72\x61\x74\x6f\x72\x20\x61\x73\x20\x6f\x63\x74\x65\x74\x20\x73\x74\x72\x69\x6e\x67\x2c\x20\x75\x6e\x63\x6f\x6d\x70\x72\x65\x73\x73\x65\x64\x20\x66\x6f\x72\x6d\x3a\xa\x20\x20\x20\x20\x20");
    for (i = 0; i < len; i++)
        fprintf(stdout, "\x25\x30\x32\x58", buf[i]);

    len =
        EC_POINT_point2oct(group, Q, POINT_CONVERSION_HYBRID, buf, sizeof buf,
                           ctx);
    if (len == 0)
        ABORT;
    if (!EC_POINT_oct2point(group, P, buf, len, ctx))
        ABORT;
    if (0 != EC_POINT_cmp(group, P, Q, ctx))
        ABORT;
    fprintf(stdout, "\xa\x47\x65\x6e\x65\x72\x61\x74\x6f\x72\x20\x61\x73\x20\x6f\x63\x74\x65\x74\x20\x73\x74\x72\x69\x6e\x67\x2c\x20\x68\x79\x62\x72\x69\x64\x20\x66\x6f\x72\x6d\x3a\xa\x20\x20\x20\x20\x20");
    for (i = 0; i < len; i++)
        fprintf(stdout, "\x25\x30\x32\x58", buf[i]);

    if (!EC_POINT_get_Jprojective_coordinates_GFp(group, R, x, y, z, ctx))
        ABORT;
    fprintf(stdout,
            "\xaA\x20\x72\x65\x70\x72\x65\x73\x65\x6e\x74\x61\x74\x69\x6f\x6e\x20\x6f\x66\x20\x74\x68\x65\x20\x69\x6e\x76\x65\x72\x73\x65\x20\x6f\x66\x20\x74\x68\x61\x74\x20\x67\x65\x6e\x65\x72\x61\x74\x6f\x72\x20\x69\x6e\xa\x4a\x61\x63\x6f\x62\x69\x61\x6e\x20\x70\x72\x6f\x6a\x65\x63\x74\x69\x76\x65\x20\x63\x6f\x6f\x72\x64\x69\x6e\x61\x74\x65\x73\x3a\xa\x20\x20\x20\x20\x20\x58\x20\x3d\x20\x30\x78");
    BN_print_fp(stdout, x);
    fprintf(stdout, "\x2c\x20\x59\x20\x3d\x20\x30\x78");
    BN_print_fp(stdout, y);
    fprintf(stdout, "\x2c\x20\x5a\x20\x3d\x20\x30\x78");
    BN_print_fp(stdout, z);
    fprintf(stdout, "\xa");

