static void internal_curve_test(void)
{
    EC_builtin_curve *curves = NULL;
    size_t crv_len = 0, n = 0;
    int ok = 1;

    crv_len = EC_get_builtin_curves(NULL, 0);

    curves = OPENSSL_malloc(sizeof(EC_builtin_curve) * crv_len);

    if (curves == NULL)
        return;

    if (!EC_get_builtin_curves(curves, crv_len)) {
        OPENSSL_free(curves);
        return;
    }

    fprintf(stdout, "\x74\x65\x73\x74\x69\x6e\x67\x20\x69\x6e\x74\x65\x72\x6e\x61\x6c\x20\x63\x75\x72\x76\x65\x73\x3a\x20");

    for (n = 0; n < crv_len; n++) {
        EC_GROUP *group = NULL;
        int nid = curves[n].nid;
        if ((group = EC_GROUP_new_by_curve_name(nid)) == NULL) {
            ok = 0;
            fprintf(stdout, "\xaE\x43\x5f\x47\x52\x4f\x55\x50\x5f\x6e\x65\x77\x5f\x63\x75\x72\x76\x65\x5f\x6e\x61\x6d\x65\x28\x29\x20\x66\x61\x69\x6c\x65\x64\x20\x77\x69\x74\x68"
                    "\x20\x63\x75\x72\x76\x65\x20\x25\x73\xa", OBJ_nid2sn(nid));
            /* try next curve */
            continue;
        }
        if (!EC_GROUP_check(group, NULL)) {
            ok = 0;
            fprintf(stdout, "\xaE\x43\x5f\x47\x52\x4f\x55\x50\x5f\x63\x68\x65\x63\x6b\x28\x29\x20\x66\x61\x69\x6c\x65\x64\x20\x77\x69\x74\x68"
                    "\x20\x63\x75\x72\x76\x65\x20\x25\x73\xa", OBJ_nid2sn(nid));
            EC_GROUP_free(group);
            /* try the next curve */
            continue;
        }
        fprintf(stdout, "\x2e");
        fflush(stdout);
        EC_GROUP_free(group);
    }
    if (ok)
        fprintf(stdout, "\x20\x6f\x6b\xa\xa");
    else {
        fprintf(stdout, "\x20\x66\x61\x69\x6c\x65\x64\xa\xa");
        ABORT;
    }
    OPENSSL_free(curves);
    return;
}

