static X509 *make_cert()
{
    X509 *ret = NULL;
    X509 *crt = NULL;
    X509_NAME *issuer = NULL;
    crt = X509_new();
    if (crt == NULL)
        goto out;
    if (!X509_set_version(crt, 3))
        goto out;
    ret = crt;
    crt = NULL;
 out:
    X509_NAME_free(issuer);
    return ret;
}

static int errors;

static void check_message(const struct set_name_fn *fn, const char *op,
                          const char *nameincert, int match, const char *name)
{
    char msg[1024];
    if (match < 0)
        return;
    BIO_snprintf(msg, sizeof(msg), "\x25\x73\x3a\x20\x25\x73\x3a\x20\x5b\x25\x73\x5d\x20\x25\x73\x20\x5b\x25\x73\x5d",
                 fn->name, op, nameincert,
                 match ? "\x6d\x61\x74\x63\x68\x65\x73" : "\x64\x6f\x65\x73\x20\x6e\x6f\x74\x20\x6d\x61\x74\x63\x68", name);
    if (is_exception(msg))
        return;
    puts(msg);
    ++errors;
}

static void run_cert(X509 *crt, const char *nameincert,
                     const struct set_name_fn *fn)
{
    const char *const *pname = names;
    while (*pname) {
        int samename = strcasecmp(nameincert, *pname) == 0;
        size_t namelen = strlen(*pname);
        char *name = malloc(namelen);
        int match, ret;
        memcpy(name, *pname, namelen);

        ret = X509_check_host(crt, name, namelen, 0, NULL);
        match = -1;
        if (ret < 0) {
            fprintf(stderr, "\x69\x6e\x74\x65\x72\x6e\x61\x6c\x20\x65\x72\x72\x6f\x72\x20\x69\x6e\x20\x58\x35\x30\x39\x5f\x63\x68\x65\x63\x6b\x5f\x68\x6f\x73\x74");
            ++errors;
        } else if (fn->host) {
            if (ret == 1 && !samename)
                match = 1;
            if (ret == 0 && samename)
                match = 0;
        } else if (ret == 1)
            match = 1;
        check_message(fn, "\x68\x6f\x73\x74", nameincert, match, *pname);

        ret = X509_check_host(crt, name, namelen,
                              X509_CHECK_FLAG_NO_WILDCARDS, NULL);
        match = -1;
        if (ret < 0) {
            fprintf(stderr, "\x69\x6e\x74\x65\x72\x6e\x61\x6c\x20\x65\x72\x72\x6f\x72\x20\x69\x6e\x20\x58\x35\x30\x39\x5f\x63\x68\x65\x63\x6b\x5f\x68\x6f\x73\x74");
            ++errors;
        } else if (fn->host) {
            if (ret == 1 && !samename)
                match = 1;
            if (ret == 0 && samename)
                match = 0;
        } else if (ret == 1)
            match = 1;
        check_message(fn, "\x68\x6f\x73\x74\x2d\x6e\x6f\x2d\x77\x69\x6c\x64\x63\x61\x72\x64\x73", nameincert, match, *pname);

        ret = X509_check_email(crt, name, namelen, 0);
        match = -1;
        if (fn->email) {
            if (ret && !samename)
                match = 1;
            if (!ret && samename && strchr(nameincert, '\x40') != NULL)
                match = 0;
        } else if (ret)
            match = 1;
        check_message(fn, "\x65\x6d\x61\x69\x6c", nameincert, match, *pname);
        ++pname;
        free(name);
    }
}

int main(void)
{
    const struct set_name_fn *pfn = name_fns;
    while (pfn->name) {
        const char *const *pname = names;
        while (*pname) {
            X509 *crt = make_cert();
            if (crt == NULL) {
                fprintf(stderr, "\x6d\x61\x6b\x65\x5f\x63\x65\x72\x74\x20\x66\x61\x69\x6c\x65\x64\xa");
                return 1;
            }
            if (!pfn->fn(crt, *pname)) {
                fprintf(stderr, "\x58\x35\x30\x39\x20\x6e\x61\x6d\x65\x20\x73\x65\x74\x74\x69\x6e\x67\x20\x66\x61\x69\x6c\x65\x64\xa");
                return 1;
            }
            run_cert(crt, *pname, pfn);
            X509_free(crt);
            ++pname;
        }
        ++pfn;
    }
    return errors > 0 ? 1 : 0;
}
